create table AER_TO_DTT_MAP
(AERID VARCHAR2(100) , 
DTTID VARCHAR2(100) 
);

create table AER_TO_FISHEYE_MAP
(AERID VARCHAR2(100) , 
FISHEYEID VARCHAR2(100) 
);

create table ASR_TO_FISHEYE_MAP
(ASRID VARCHAR2(100) , 
FISHEYEID VARCHAR2(100) 
);

create table DTT_TO_FISHEYE_MAP
(DTTID VARCHAR2(100) , 
FISHEYEID VARCHAR2(100) 
);

create table FISHEYE_IGNORED
(CSID VARCHAR2(100) 
);

create table FISHEYE_REVS
(AUTHOR VARCHAR2(100) , 
COMMENTS VARCHAR2(4000) , 
CSID VARCHAR2(100) , 
ID NUMBER(22) NOT NULL , 
PATH VARCHAR2(200) , 
PROJECT VARCHAR2(50) , 
REVISION VARCHAR2(100) ,
constraint FISHEYE_REVS_PK primary key (ID) 
);

create table SHAREPOINT_AER
(AERID VARCHAR2(100) , 
ASSIGNMENT VARCHAR2(100) , 
ID NUMBER(22) NOT NULL , 
ITEMNUMBER VARCHAR2(1000) , 
OWNER VARCHAR2(100) , 
SYSTEM VARCHAR2(100) , 
TEAM VARCHAR2(100) , 
TITLE VARCHAR2(1000) ,
constraint SHAREPOINT_AER_PK primary key (ID) 
);

create table SHAREPOINT_ASR
(ASRTYPE VARCHAR2(100) , 
GROUPNM VARCHAR2(100) , 
ID NUMBER(22) NOT NULL , 
ITEMID VARCHAR2(100) , 
ITEMNUMBER VARCHAR2(1000) , 
PRODUCT VARCHAR2(100) , 
TITLE VARCHAR2(1000) ,
constraint SHAREPOINT_ASR_PK primary key (ID) 
);

create table SHAREPOINT_DTT
(AERNUMBER VARCHAR2(100) , 
ASSIGNMENT VARCHAR2(1000) , 
DEFECTNAME VARCHAR2(1000) , 
ID NUMBER(22) NOT NULL , 
ITEMID VARCHAR2(100) , 
REPORTER VARCHAR2(100) , 
STATUS VARCHAR2(100) , 
TEAM VARCHAR2(100) ,
constraint SHAREPOINT_DTT_PK primary key (ID) 
);

create table SHAREPOINT_PRE_AER
(ASSIGNED VARCHAR2(100) , 
COMMENTS VARCHAR2(1000) , 
ID NUMBER(22) NOT NULL , 
ISSUEID VARCHAR2(100) , 
REF_AER NUMBER(22) , 
STATUS VARCHAR2(100) , 
TEAM VARCHAR2(100) , 
TITLE VARCHAR2(1000) ,
constraint SHAREPOINT_PRE_AER_PK primary key (ID) 
);

create table SHAREPOINT_SCOPE
(ID NUMBER(22) NOT NULL , 
ISSUEID VARCHAR2(100) , 
ITEMTYPE VARCHAR2(100) , 
REQUEST VARCHAR2(1000) , 
STATUS VARCHAR2(100) , 
TEAM VARCHAR2(100) ,
constraint SHAREPOINT_SCOPE_PK primary key (ID) 
);


alter table SHAREPOINT_PRE_AER add constraint SHAREPOINT_PRE_AER_FK foreign key (REF_AER) 
references SHAREPOINT_AER(ID);
